package com.example.myapp.core.data.remoteimport com.example.myapp.todo.data.DateAdapterimport com.google.gson.GsonBuilderimport com.squareup.moshi.Moshiimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryimport retrofit2.converter.moshi.MoshiConverterFactoryobject Api {    private val url = "10.152.0.155:3000"    //private val url = "192.168.1.8:3000"    private val httpUrl = "http://$url/"    val wsUrl = "ws://$url"    private var gson = GsonBuilder()        .setDateFormat("yyy-MM-dd'T'HH:mm").create()    private var moshiBuilder = Moshi.Builder()        .add(DateAdapter()).build()    val tokenInterceptor = TokenInterceptor()    val okHttpClient = OkHttpClient.Builder().apply {        this.addInterceptor(tokenInterceptor)    }.build()    val retrofit = Retrofit.Builder()        .baseUrl(httpUrl)        //.addConverterFactory(MoshiConverterFactory.create(moshiBuilder))        .addConverterFactory(GsonConverterFactory.create(gson))//        .client(okHttpClient) // does not work in android with the latest libs        .build()}